generated.css:
*{box-sizing: border-box}
*:before,*::after{box-sizing: border-box}
*{margin: 0; padding: 0}
ul{list-style: none}
a{color: inherit;text-decoration: none;}
img{max-width: 100%;max-height: 100%;}
.icon {
    width: 1em;
    height: 1em;
    vertical-align: -0.15em;
    fill: currentColor;
    overflow: hidden;
  }


body {
    /*background-color: #eee; 添加背景图*/

    background-image: url(40939f225777d81880183f381d599249.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    background-attachment: fixed;
}



.globalHeader{
    margin: 20px 20px;
    
}
@media(min-width:500px){
    .globalHeader {
        margin: 60px 0 100px 0
    }
}
.searchForm {
    display: flex;
    justify-content: space-between;
    max-width: 400px;
}
@media (min-width: 500px){
    .searchForm {
        max-width: 400px;
        margin-left:  auto;
        margin-right: auto;
    }

}


.searchForm > input {
    width: 100%;
    margin-right: 10px;
    height: 40px;   
    padding: 0 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
}
.searchForm > button{
    /* 搜索不换行 */
    white-space: nowrap;
    padding: 0 26px;
    border: none;
    background: #3280FC;
    border-radius: 4px;
    color: white;
    font-size: 16px;
}




.globalMain{
    max-width: 900px;
    margin-left:  auto;
    margin-right: auto;
}

.siteList{
    display: flex;
    flex-wrap: wrap;
    /* 换行 */   
    margin: 20px;
    justify-content: space-between;
}
@media (min-width: 500px){
    .siteList {
        margin-right: -25px;
        margin-left: 0;
        justify-content: flex-start;
    }
}
.siteList > li{  
    margin-bottom: 10px;


}
@media(min-width: 500px){
    .siteList > li {
        margin-right: 25px;
    }
}
.siteList  .site {
    width: 160px;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    /* 上下分布 */
    background: white;
    border: 1px solid #ddd;
    border-radius: 4px;
    padding: 20px 0;
    position: relative;
    cursor: pointer;
    
}
.siteList  .site > .logo{
    width: 48px;
    height: 48px;
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 48px;
    text-transform: uppercase;

}
.siteList  .site > .link{
    font-size: 16px;
    margin-top: 8px;
}
.siteList  .site > .close {
    position: absolute;
    right: 10px;
    top: 5px;
    display: none;
}

.siteList  .site:hover> .close{
    display: block;
    cursor: default;
}

.siteList > li > .addButton{
    width: 160px;
    border: 1px solid #ddd;
    background-color: white;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    padding: 20px 0;
    border-radius: 4px;
}

.siteList .addButton .icon {
    width: 48px;
    height: 48px;
    
}
.siteList .addButton >.text {
    font-size: 16px;
    margin-top: 8px;
}

.siteList .addButton .iconWrapper {
   
    width: 48px;
    height: 48px;
    display: flex;
    justify-content: center;
    align-items: center;
}



.globalFooter > ul {
    margin: 6px;
    right: 0;
    bottom: 0;
    position: absolute;
    border-radius:6px;
}

@media(max-width: 500px){
    .globalFooter>ul {
        display: none;
    }
}


.globalFooter>ul:hover {
   display: block;
    background: #6b2b02;
}


.globalFooter >ul>li{
    display: flex;
    justify-content: center;
}
